import React from 'react'
import {Formik, Form, Field, ErrorMessage} from 'formik'
import * as Yup from 'yup';

function CreatePost() {

    const initialValues = {
        title: "",
        postText:"",
        username:"",
    }
    const validationSchema= Yup.object().shape({
        title: Yup.string().required(),
        postText: Yup.string.required() ,
        username:Yup.string.min(3).max().required
    })

    const onSubmit=(data)=>{
        console.log(data);

    }
  return (
    <div className='createPostPage'>
        <Formik initialValues={initialValues} onSubmit={onSubmit}>

            <Form className='formContainer'>
                <label>Title:</label>
                <Field id="inputCreatePost" 
                name="title"
                 placeholder="Input yageys ya" autocomplete="off"/>

                <label>PostText:</label>
                <Field id="inputCreatePost" name="postText" placeholder="Input yageys ya"/>

                <label>username:</label>
                <Field id="inputCreatePost" name="username" placeholder="Input yageys ya"/>

                <button type='submit'>Submit Post</button>
            </Form>

        </Formik>
    </div>
  )
}

export default CreatePost
