import React from 'react'
import axios from 'axios'; // untuk hubungin api backend dengan frontend
import { useEffect, useState } from 'react'; 
import { useNavigate } from "react-router-dom";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css"; // Import gaya dasar Swiper
import "swiper/css/navigation"; // Jika menggunakan navigasi
import "swiper/css/pagination"; // Jika menggunakan pagination
import { Navigation, Pagination, Autoplay } from "swiper/modules"; // Import modul tambahan Swiper

function Home() {
  const [listOfPosts, setListOfPosts] = useState([]);
  let navigate = useNavigate();

  useEffect(() => {
    axios.get("http://localhost:3001/posts").then((response) => {
      setListOfPosts(response.data);
    });
  }, []);

  return (
    <div className="container my-20">
      <Swiper
        modules={[Navigation, Pagination, Autoplay]}
        spaceBetween={80} // Jarak antar slide
        slidesPerView={2} // Menampilkan satu slide per tampilan
        navigation // Menampilkan tombol navigasi
        pagination={{ clickable: true }} // Menampilkan pagination
        autoplay={{ delay: 3000 }} // Otomatis berganti slide setiap 3 detik

      >
        {listOfPosts.map((value, key) => (
          <SwiperSlide key={key}>
            <div 
              className="post bg-white shadow-md rounded p-4" 
              onClick={() => navigate(`/post/${value.id}`)}
            >
              <div className="title font-bold text-lg mb-2">{value.title}</div>
              <div className="body text-gray-700 mb-4">{value.postText}</div>
              <div className="footer text-gray-500 text-right">{value.username}</div>
            </div>
          </SwiperSlide>
        ))}
      </Swiper>
    </div>
  );
}

export default Home;
