import './App.css';
import axios from 'axios'; //untuk hubungin api backend dengan frontend
import { useEffect, useState } from 'react'; // untk menjalankan sebuah perintah dari API dengan ada dependencies


function App() {

 const[listOfPosts,setListOfPosts]=useState([]);//kosongin dan kasih array kosong karna return array



useEffect(()=>{
  axios.get("http://localhost:3001/posts").then((response)=>{
    setListOfPosts(response.data);

    // console.log(response.data); // untuk tampilan data di console
  });
},[]);

//code untuk nampilin list of array data
  return (
    <div className="App">

      {listOfPosts.map((value,key)=>{
        return(
          <div className='post'>
            <div className='title'>{value.title}</div>
            <div className='body'>
              {value.postText}</div>
            <div className='footer'> {value.username}</div>

              </div>
        )
      })}
    </div>
  );
}

export default App;
