import React from 'react'
import axios from 'axios'; //untuk hubungin api backend dengan frontend
import { useEffect, useState } from 'react'; 
import {useHi} from "react-router-dom";


function Home() {
    const [listOfPosts,setListOfPosts] = useState([]);
    let history =useHistory()

    useEffect(()=>{
        axios.get("http://localhost:3001/posts").then((response)=>{
          setListOfPosts(response.data);

          // console.log(response.data); // untuk tampilan data di console
        });
      },[]);



  return (
    <div>
       {listOfPosts.map((value,key)=>{
        return(
          <div className='post' onClick={()=>{history.push(`/post/${value.id}`)}}>
            <div className='title'>{value.title}</div>
            <div className='body'>
              {value.postText}</div>
            <div className='footer'> {value.username}</div>

              </div>
        ) 
    })}
    </div>
  )
}

export default Home
