import {Formik, Form, Field, ErrorMessage} from 'formik'
import * as Yup from 'yup';
import axios from 'axios';

function Registration() {

    const initialValues = {
        username:"",
        password:""
    }
    const validationSchema= Yup.object().shape({
        username:Yup.string().min(3).max(16).required(),
        password:Yup.string().min(6).max(10).required(),
    })

    const onSubmit =(data)=>{
        axios.post(" http://localhost:3001/auth",data).then(()=>{
            console.log(data);
        })

    }


  return (
    <div className='"w-full h-screen flex justify-center items-center"'>
        <Formik initialValues={initialValues} onSubmit={onSubmit} validationSchema={validationSchema}>
        <div className='bg-stone-700 w-5/12 h-3/6 flex justify-center items-center flex-col'>

            <div className='flex justify-center flex-col gap-5 w-full px-8'>


            <Form className=''>
            <label>username:</label>
            <ErrorMessage name="username" component="span"/>
            <Field name="username" placeholder="Input yageys ya"/>

            <label>password:</label>
            <ErrorMessage name="password" component="span"/>
            <Field name="password" placeholder="Input yageys ya" type="password"/>


        </Form>
            </div>

    <div className="flex justify-center">
      <button className="mt-4 bg-white text-blue-600 px-4 py-2 rounded" onClick={login} >
        Register
      </button>


    </div>

        </div>


    </Formik>
    </div>
  )

  }


export default Registration
